package test;

public class Evennum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 40;
		
		System.out.println("Enter the Number: ");
		
		
		if(num%2==0) {
			
			System.out.println("Even Number");
		}else {
			
			System.out.println("Odd Number");
		}


	}

}
